var link=document.querySelector(".contact-button"),popup=document.querySelector(".pop-up"),close=popup.querySelector(".pop-up-close"),form=popup.querySelector(".pop-up-form"),yourName=popup.querySelector(".pop-up-form-input-name"),eMail=popup.querySelector(".pop-up-form-input-email"),eMailText=popup.querySelector(".pop-up-form-input-text"),storageName=localStorage.getItem("yourName"),storageMail=localStorage.getItem("eMail");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("pop-up-show"),storageName&&storageMail?(yourName.value=storageName,eMail.value=storageMail,eMailText.focus()):yourName.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("pop-up-show"),popup.classList.remove("pop-up-error")}),form.addEventListener("submit",function(e){yourName.value&&eMail.value&&eMailText.value?(localStorage.setItem("yourName",yourName.value),localStorage.setItem("eMail",eMail.value)):(e.preventDefault(),popup.classList.add("pop-up-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("pop-up-show")&&popup.classList.remove("pop-up-show")});